import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { RestApiService } from '../services/rest-api.service';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {
  Journey: any = [];
  origin = new FormControl('');
  destination = new FormControl('');
  constructor(public restApi: RestApiService) { }
  searchJourney() {

    if (this.origin.value !== "" || this.destination.value !== "") {
      this.restApi.searchJourney().subscribe((data: any) => {
        this.Journey = data.filter((journey: any) => {
          return journey.departureStation === this.origin.value || journey.arrivalStation === this.destination.value;
        });
        if (this.Journey.length > 0) {
          alert('La busqueda ha traido resultado');
        } else {
          alert('No han entrado vuelos que concidan con la información agregada');
        }
      });
    } else {
      alert('Se require que se dirigencie los dos campos');
    }
  }
}
