import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { RestApiService } from '../services/rest-api.service';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {
  Journey: any = [];
  origin = new FormControl('BOG');
  destination = new FormControl('MEX');
  constructor(public restApi: RestApiService) { }

  convertUppercaseOrigin() {
    this.origin.setValue(this.origin.value?.toUpperCase() === undefined ? null : this.origin.value?.toUpperCase());
  }

  convertUppercaseDestination() {
    this.destination.setValue(this.destination.value?.toUpperCase() === undefined ? null : this.destination.value?.toUpperCase());
  }


  searchJourney() {

    if (this.origin.value !== "" || this.destination.value !== "") {

      if ((this.origin.value === this.destination.value)) {
        alert('El origin y el distino no pueden ser iguales');
        return;
      }
      this.restApi.searchJourney(this.origin.value, this.destination.value).subscribe((data: any) => {

        console.log('Journey',data);

        this.Journey = data;
        if (this.Journey.length > 0) {
          alert('La busqueda ha traido resultado');
          return
        } else {
          alert('No han entrado vuelos que concidan con la información agregada');
          return
        }
      });
    } else {
      alert('Se require que se dirigencie al menos el campo de origin');
      return
    }
  }
}

<div class="card">
  <img src="imagen.jpg" alt="Imagen de la card">
  <div class="card-content">
    <h2>Título de la card</h2>
    <label>Enter origin city</label>
    <input [formControl]="origin" #input maxlength="3" placeholder="BOG" (ngModelChange)="convertUppercaseOrigin()"
      type="text">
    <p *ngIf="origin.value?.length === 0">Por favor ingrese su origin</p>
    <p *ngIf="origin.value?.length === 4">Maximo 3 caracteres</p>
  </div>
  <div class="card-actions">
    <button>Acción 1</button>
    <button>Acción 2</button>
  </div>
</div>
<form>
  <div class="container">
    <div class="row">
      <div class="col">

      </div>
      <div class="col">
        <label>Enter destination city</label>
        <input [formControl]="destination" matInput #input maxlength="3" placeholder="MDE" type="text"
          (ngModelChange)="convertUppercaseDestination()">
        <p *ngIf="destination.value?.length === 0">Por favor ingrese su destination</p>
        <p *ngIf="destination.value?.length === 4">Maximo 3 caracteres</p>
      </div>
      <div class="col">
        <button mat-raised-button color="warn" (click)="$event.preventDefault();searchJourney()">Search</button>
      </div>
    </div>
  </div>
</form>

<h2>Result</h2>
<div class="card-container" *ngIf="Journey.length > 0">
  <div *ngFor="let item of Journey">
    <div class="flight-card">
      <div class="flight-card-header">
        <h2> {{item.origin}} - {{item.destination}}</h2>
      </div>
      <div class="flight-card-body">
        <div class="flight-card-price">
          <p>Price:</p>
          <h3>${{item.price}}</h3>
        </div>
      </div>
    </div>
    <div class="flight-card">
      <div class="flight-card-info">
        <div *ngFor="let flight of item.flights">
          <h3><b>FlightCarrier:</b></h3>{{flight.transport.flightCarrier}}
          <h3><b>FlightNumber:</b>{{flight.transport.flightNumber}}</h3>
          <h2> {{flight.origin}} - {{flight.destination}}</h2>
          <div class="flight-card-price">
            <p>Price:</p>
            <h3>${{flight.price}}</h3>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
