import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { retry, catchError, map } from 'rxjs/operators';
import { Journey } from '../models/Journey';
import { Flight } from '../models/Flight';
import { Transport } from '../models/Transport';

@Injectable({
  providedIn: 'root'
})
export class RestApiService {
  // Define API
  apiURL = "https://recruiting-api.newshore.es/api/flights/2";
  constructor(private http: HttpClient) { }

  flights: Flight[] = [];
  httpOptions = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json',
    }),
  };


  searchJourney(origin: any, destination: any): Observable<Journey[]> {

    return this.http.get<Journey[]>(this.apiURL).pipe(map((response: any) => {
      return response.map((item: any) => {
        let newFlight;
        if (item.departureStation !== origin && item.arrivalStation !== destination) {

          const transport = new Transport(item.flightCarrier, item.flightNumber);
          newFlight: Flight = {
            "departureStation":item.departureStation, 
            "arrivalStation":item.arrivalStation, 
            "price":item.price, 
            "transport":transport
          };
          this.flights.push(newFlight);

        }
        const journey = new Journey(item.departureStation, item.arrivalStation, item.price, flights);
        return journey;
      });
    })
    );

  }

  // Error handling
  handleError(error: any) {
    let errorMessage = '';
    if (error.error instanceof ErrorEvent) {
      // Get client-side error
      errorMessage = error.error.message;
    } else {
      // Get server-side error
      errorMessage = `Error Code: ${error.status}\nMessage: ${error.message}`;
    }
    alert(errorMessage);
    return throwError(() => {
      return errorMessage;
    });
  }
}
