import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { retry, catchError } from 'rxjs/operators';
import { IJourney } from '../interfaces/Journey';
import swal from 'sweetalert';


@Injectable({
  providedIn: 'root'
})
export class RestApiService {
  // Define API
  apiURL = "https://recruiting-api.newshore.es/api/flights/";
  constructor(private http: HttpClient) {}

  httpOptions = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json',
    }),
  };

  searchJourney(origin: any, destination: any): Observable<IJourney> {
    return this.http
      .get<IJourney>(this.apiURL + '/Journey?origin='+origin+'&destination='+destination)
      .pipe(retry(1), catchError(this.handleError));
  }

  // Error handling
  handleError(error: any) {
    let errorMessage = '';
    if (error.error instanceof ErrorEvent) {
      // Get client-side error
      errorMessage = error.error.message;
    } else {
      // Get server-side error
      errorMessage = `Error Code: ${error.status}\nMessage: ${error.message}`;
    }
    swal("Oops!", errorMessage, "error");
    return throwError(() => {
      return errorMessage;
    });
  }
}
